properties.entry:
    BEGIN
      EXECUTE IMMEDIATE 'DROP USER ${kuali.jdbc.oracle.username} CASCADE';
      EXCEPTION WHEN OTHERS THEN
      IF SQLCODE != ${kuali.jdbc.oracle.error.sqlcode.userDoesNotExist} THEN
        RAISE;
      END IF;
    END;
    /

    CREATE USER ${kuali.jdbc.oracle.username}
    IDENTIFIED BY ${kuali.jdbc.oracle.password}
    DEFAULT TABLESPACE ${kuali.jdbc.oracle.defaultTablespace}
    TEMPORARY TABLESPACE ${kuali.jdbc.oracle.temporaryTablespace}
    QUOTA ${kuali.jdbc.oracle.quota} ON ${kuali.jdbc.oracle.defaultTablespace}
    /

    DROP DATABASE IF EXISTS ${kuali.jdbc.mysql.database}
    /

    GRANT USAGE ON *.* TO '${kuali.jdbc.mysql.username}'@'%' IDENTIFIED BY '${kuali.jdbc.mysql.password}'
    /

    DROP USER ${kuali.jdbc.dba.mysql.username}
    /

    CREATE DATABASE IF NOT EXISTS ${kuali.jdbc.mysql.database}
    DEFAULT CHARACTER SET '${kuali.jdbc.mysql.character.set}'
    DEFAULT COLLATE '${kuali.jdbc.mysql.collate}';
    /
