properties.entry:
    DROP DATABASE IF EXISTS ${jdbc.database}
    /

    GRANT USAGE ON *.* TO '${jdbc.username}'@'%' IDENTIFIED BY '${jdbc.password}'
    /

    DROP USER ${jdbc.username}
    /

    CREATE DATABASE IF NOT EXISTS ${jdbc.database}
    DEFAULT CHARACTER SET '${jdbc.mysql.character.set}'
    DEFAULT COLLATE '${jdbc.mysql.collate}';
    /
