properties.entry:
    BEGIN
      EXECUTE IMMEDIATE 'DROP USER ${jdbc.username} CASCADE';
      EXCEPTION WHEN OTHERS THEN
      IF SQLCODE != ${jdbc.oracle.error.sqlcode.userDoesNotExist} THEN
        RAISE;
      END IF;
    END;
    /

    CREATE USER ${jdbc.username}
    IDENTIFIED BY ${jdbc.password}
    DEFAULT TABLESPACE ${jdbc.oracle.defaultTablespace}
    TEMPORARY TABLESPACE ${jdbc.oracle.temporaryTablespace}
    QUOTA ${jdbc.oracle.quota} ON ${jdbc.oracle.defaultTablespace}
    /
